mapper.update:
UPDATE category


        name = #{name},


        sort = #{sort},


        type = #{type},


        status = #{status},


        update_time = #{updateTime},


        update_user = #{updateUser}


        create_time = #{createTime},


        create_user = #{createUser}



WHERE id = #{id}

mapper.select:
SELECT * FROM category where status = 1
and type = #{type}
ORDER BY sort ASC, create_time DESC
